package com.example.tmoproductos

import android.content.Context
import android.database.sqlite.SQLiteOpenHelper
import android.database.sqlite.SQLiteDatabase
import android.annotation.SuppressLint
import android.database.Cursor

class DBHelper(context: Context): SQLiteOpenHelper(context, "mibase.db", null, 1) {

    override fun onCreate(db: SQLiteDatabase) {
        // Crear la tabla 'producto'

        val query1 = """
            CREATE TABLE producto (
                id_producto INTEGER PRIMARY KEY AUTOINCREMENT NOT NULL,
                nombre TEXT NOT NULL,
                precio DOUBLE NOT NULL,
                descripcion TEXT,
                imagen TEXT
            );
        """
        db.execSQL(query1) //ejecutamos query 1


        //TODO: Implementar un metodo mas eficaz


        // Clase  tipo data Producto para crear onjetos de cda producto
        data class Producto(val nombre: String, val precio: Double, val descripcion: String?, val imagen: String?)

        // Arreglo de productos con sus características
        val productos = arrayOf(
            Producto("Pluma Azul", 25.0, "Pluma de alta calidad de tinta azul.",""),
            Producto("Cinta Adhesiva", 30.0, "Cinta adhesiva resistente, ideal para uso en oficinas y escuelas.",""),
            Producto("Goma", 10.0, "Goma de borrar con una excelente calidad, ideal para lápiz.",""),
            Producto("Lapiz", 5.0, "Lápiz de madera con buena punta para escritura precisa.",""),
            Producto("Tijeras", 27.50, "Tijeras de acero inoxidable para cortar con precisión.",""),
            Producto("Libreta Cuadro Chico", 30.0, "Libreta de cuadros pequeños para tomar notas con facilidad.",""),
            Producto("Impresora", 900.0, "Impresora de alta calidad, ideal para oficina y trabajo en casa.",""),
            Producto("Plumon Verde", 26.0, "Plumón verde para escribir y marcar de forma visible.",""),
            Producto("Regla de Ángulo", 25.0, "Regla con ángulo para mediciones precisas en proyectos de geometría.",""),
            Producto("Folder Amarillo", 5.0, "Folder amarillo para organizar documentos de manera eficiente.",""),
            //Producto("Cámara", 3050.0,"Cámara de alta cilidad para momentos inolvidables","")
        )
        // Insertar los productos en la base de datos con un ciclo for
        for (producto in productos) { //iterar sobre cada producto
            val query = """
                INSERT INTO producto (nombre, precio, descripcion, imagen)
                VALUES ('${producto.nombre}', ${producto.precio}, '${producto.descripcion}', '${producto.imagen}');
            """
            db.execSQL(query)//anadir producto a tabla
        }
    }

    // Función para obtener la lista completa de productos desde la base de datos
    fun obtenerProductos(): List<Producto> {
        val listaProductos = mutableListOf<Producto>()
        val db = this.readableDatabase
        val cursor: Cursor = db.rawQuery("SELECT * FROM producto", null)

        // Iterar sobre los registros recuperados
        @SuppressLint("Range")
        while (cursor.moveToNext()) {
            val nombre = cursor.getString(cursor.getColumnIndex("nombre"))
            val precio = cursor.getDouble(cursor.getColumnIndex("precio"))
            val descripcion = cursor.getString(cursor.getColumnIndex("descripcion"))
            val imagenBase64 = cursor.getString(cursor.getColumnIndex("imagen"))

            listaProductos.add(Producto(nombre, precio, descripcion,imagenBase64)) //se insatancia cada objeto de la clase Data Producto que se encuentra en Main
        }

        cursor.close()
        db.close()

        return listaProductos //retornamos los productos
    }

    override fun onUpgrade(db: SQLiteDatabase?, oldVersion: Int, newVersion: Int) {
        // Lógica de actualización si es necesario
    }
}